const styles = theme => ({
    inputRoot: {
        fontSize: 16
    },
    input: {
        padding: "10px 14px 11px",
        width: "98%"
    },
    inputLabel: {
        fontSize: 16,
        transform: "translate(14px, 10px) scale(1)"
    },
    multiline: {
        padding: "8px 14px 8px 0px"
    }
});

export default styles;
