import React, { Component } from 'react';
import MUIDataTable from "mui-datatables";
import Button from "@material-ui/core/Button";
import ModalWrapper from "../../components/ModalWrapper/ModalWrapper"

class EntriesManager extends Component {
    constructor(props) {
        super(props);
        this.state = {
            addEntryModalShowing: false
        }
    }
    componentDidMount() {
        this.props._fetchEntries();
        console.log(this.props.entries);
    }
    // openAddEntryModal = () => {
    //     this.setState({ addEntryModalShowing: true });
    // }
    closeAddEntryModal = () => {
        this.props._closeAddEntryModal();
    }
    render() {
        const { addEntryShowing, entries, _openAddEntryModal, _closeAddEntryModal,addEntryModalShowing } = this.props;
        const options = {
            filterType: "dropdown",
            responsive: "scroll"
        };
        const columns = ["Inventory", "Username", "created_at", "taken", "consumed", "returned", "remaining"];

        return (
            <div>
                <div>
                    <Button color="primary" onClick={_openAddEntryModal}>Add Entry</Button>
                </div>
                <MUIDataTable
                    title={"ACME Employee list"}
                    data={entries}
                    columns={columns}
                    options={options}
                />
                <ModalWrapper
                    title={"Add Entry"}
                    isOpen={addEntryModalShowing}
                    minWidth={720}
                    showBottomToolbar={false}
                    showCloseIcon={true}
                    onClose={this.closeAddEntryModal}
                    showResizeOptions={false}
                    // toggleWindowSize={this.toggleWindowSize}
                    // isMaximize={isMaximize}
                ></ModalWrapper>
            </div>
        );
    }
}

export default EntriesManager;
