import React, { Component } from 'react';
import MUIDataTable from "mui-datatables";

class EntriesManager extends Component {
    componentDidMount(){
        this.props._fetchEntries();
        console.log(this.props.entries);
    }
    openAddEntryModal = () => {
        this.setState({addEntryModalShowing:true});
    }
    closeAddEntryModal = () => {
        this.setState({addEntryModalShowing:false});
    }
    render() {
        console.log(this.props.entries,"kuch bata")
        const options = {
            filterType: "dropdown",
            responsive: "scroll"
        };
        const columns = ["Inventory", "Username", "taken", "consumed", "returned", "remaining"];

        return (
            <div>
                <div>
                    <Button color="primary" onClick={this.openModal}>Add Entry</Button>
                </div>
                <MUIDataTable
                    title={"ACME Employee list"}
                    data={this.props.entries}
                    columns={columns}
                    options={options}
                />
            </div>
        );
    }
}

export default EntriesManager;
